use dioxus::prelude::*;

macro_rules! icon {
    ($name: ident, $path: expr) => {
        pub fn $name(cx: Scope) -> Element {
            cx.render(rsx! {
                svg {
                    width: "24",
                    height: "24",
                    view_box: "0 0 24 24",
                    path {
                        fill_rule: "evenodd",
                        clip_rule: "evenodd",
                        d: $path
                    }
                }
            })
        }
    };
}

